          dataType: 'json',
          success: function(data, textStatus) {
            $.map(data.results, function(val, i) {
              results.push({
                label: val.title,
                url: val.url
              });
            });
            response(results);
          }
        });
      };

      var sectionSearch = function(request, response) {
        var articleName = request.term.split('#')[0];
        var articleURL = getArticleURL(articleName);

        if (!articleURL) {
          return;
