          var link = $external.val();
          if (link) {
            if (link.indexOf('http') !== 0) {
              link = 'http://' + link;
            }
            me.openTag = '[' + link + ' ';
            if (text) {
              me.openTag += text + '] ';
              me.closeTag = '';
              me.defaultText = '';
            }
          } else if (text) {
            me.defaultText = text;
          }
        }

        me.handleClick(e);
        kbox.close();
      });

      if (selectedText) {
        // If there user has selected text, lets default to it being
        // the Article Title.
        $html.find('input[name="internal"]').val(selectedText).focus();
      }

      kbox = new KBox($html, {
        title: this.name,
        destroy: true,
        modal: true,
        id: 'link-modal',
