  };

// Called when the user touches something.
  ShowFor.prototype.onUIChange = function() {
    this.updateState();
    this.showAndHide();
    this.persist();
  };

// Stores the current object state in the url hash and/or sessionStorage.
  ShowFor.prototype.persist = function() {
